import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

public class SteadyGeneTest {

  SteadyGene steadyGene;

  @BeforeEach
  void setUp() {
    steadyGene = new SteadyGene();
  }

  @Test
  void demo() {
    Assertions.assertEquals(0, steadyGene.minLengthOfSubStringForSteadyGene(""));
  }

  @Test
  void demo2() {
    Assertions.assertEquals(5, steadyGene.minLengthOfSubStringForSteadyGene("GAAATAAA"));
  }

  @Test
  void demo3() {
    Assertions.assertEquals(1393, steadyGene.minLengthOfSubStringForSteadyGene(""));
  }


}